package bkap.crud1.controller;import bkap.crud1.entity.User;import bkap.crud1.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.*;import java.util.List;import java.util.Optional;@Controllerpublic class UserController {  @Autowired private UserService userService;  @RequestMapping("/index  ")  public String index(Model model) {    List<User> users = userService.getAllUser();    model.addAttribute("users", users);    return "index";  }  @RequestMapping(value = "add")  public String addUser(Model model){    model.addAttribute("user", new User());    return "addUser";  }  @GetMapping(value = "/edit")  public String editUser(@RequestParam("id") Long userId, Model model){    Optional<User> userEdit = userService.findUserByID(userId);    userEdit.ifPresent(user -> model.addAttribute("user", user));    return "editUser";  }  @PostMapping(value = "save")  public String save (User user){    userService.saveUser(user);    return "redirect:/";  }  @GetMapping(value = "/delete")  public String deleteUser(@RequestParam("id") Long userId, Model model){    userService.deleteUser(userId);    return "redirect:/";  }}