package bkap.crud1.entity;import javax.persistence.*;@Entity@Table(name = "user")public class User {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name = "name")    private String name;    @Column(name = "email")    private String email;    @Column(name = "phone")    private String phone;    public User() {    }    public User(String name, String email, String phone) {        this.name = name;        this.email = email;        this.phone = phone;    }    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }    public String getPhone() {        return phone;    }    public void setPhone(String phone) {        this.phone = phone;    }}