package bkap.crud1.service.impl;import bkap.crud1.entity.User;import bkap.crud1.repository.UserRepository;import bkap.crud1.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.*;import java.util.List;import java.util.Optional;@Servicepublic class UserServiceImpl implements UserService {    @Autowired private UserRepository userRepository;    @Override    public List<User> getAllUser() {        return (List<User>) userRepository.findAll();    }    @Override    public void saveUser(User user) {         userRepository.save(user);    }    @Override    public void deleteUser(Long id) {        userRepository.deleteById(id);    }    @Override    public Optional<User> findUserByID(Long id) {        return userRepository.findById(id);    }}