package com.example.securitydemo.models;import javax.persistence.*;@Entity@Table(name = "login")public class User {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private int id;    private String username;    private String password;    private boolean status;    private String roles;    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getUsername() {        return username;    }    public void setUsername(String username) {        this.username = username;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public boolean isStatus() {        return status;    }    public void setStatus(boolean status) {        this.status = status;    }    public String getRoles() {        return roles;    }    public void setRoles(String roles) {        this.roles = roles;    }}