package Bai2;import java.util.*;public class Main {  public static void main(String[] args) {    Scanner sc = new Scanner(System.in);    Set<City> c = new HashSet<>();    while (true) {      System.out.println("**********************MENU*********************");      System.out.println("1.Nhap danh sach gom n thanh pho");      System.out.println("2. Hien thi danh sach thanh pho");      System.out.println("3. Hien thi danh sach sap xep tang dan");      System.out.println("4. Thoat");      System.out.print("Lua chon: ");      int choice = Integer.parseInt(sc.nextLine());      switch (choice) {        case 1:          System.out.print("Nhap n: ");          int n = Integer.parseInt(sc.nextLine());          for (int i = 0; i < n; i++) {            City ct = new City();            System.out.print("ID: ");            ct.setCityId(sc.nextLine());            System.out.print("Name: ");            ct.setCityName(sc.nextLine());            System.out.print("Status: ");            ct.setCityStatus(Boolean.parseBoolean(sc.nextLine()));            c.add(ct);          }          break;        case 2:          for (City c1 : c) {            System.out.print(                "ID: "                    + c1.getCityId()                    + " Name: "                    + c1.getCityName()                    + " Status: "                    + c1.isCityStatus()                    + "\n");          }          break;        case 3:          ArrayList<City> listC = new ArrayList<>(c);          Collections.sort(listC, new Comparator<City>() {            @Override            public int compare(City o1, City o2) {              return o1.getCityId().compareTo(o2.getCityId());            }          });          for (City c1 : listC) {            System.out.print(                    "ID: "                            + c1.getCityId()                            + " Name: "                            + c1.getCityName()                            + " Status: "                            + c1.isCityStatus()                            + "\n");          }          break;        case 4:          System.out.println("Bye!!!");          System.exit(0);        default:          System.out.println("0-4 please!");      }    }  }}