package Bai3;public class Nation {    private String nationId, nationName;    private boolean nationStatus;    public String getNationId() {        return nationId;    }    public void setNationId(String nationId) {        this.nationId = nationId;    }    public String getNationName() {        return nationName;    }    public void setNationName(String nationName) {        this.nationName = nationName;    }    public boolean isNationStatus() {        return nationStatus;    }    public void setNationStatus(boolean nationStatus) {        this.nationStatus = nationStatus;    }}