package Bai4;import java.io.Serializable;public class Product implements Serializable {    private String proId, proName, producer;    private int yearMarking;    private float price;    public Product() {    }    public Product(String proId, String proName, String producer, int yearMarking, float price) {        this.proId = proId;        this.proName = proName;        this.producer = producer;        this.yearMarking = yearMarking;        this.price = price;    }    public String getProId() {        return proId;    }    public void setProId(String proId) {        this.proId = proId;    }    public String getProName() {        return proName;    }    public void setProName(String proName) {        this.proName = proName;    }    public String getProducer() {        return producer;    }    public void setProducer(String producer) {        this.producer = producer;    }    public int getYearMarking() {        return yearMarking;    }    public void setYearMarking(int yearMarking) {        this.yearMarking = yearMarking;    }    public float getPrice() {        return price;    }    public void setPrice(float price) {        this.price = price;    }    @Override    public String toString() {        return  "proId='" + proId + '\'' +                ", proName='" + proName + '\'' +                ", producer='" + producer + '\'' +                ", yearMarking=" + yearMarking +                ", price=" + price;    }}