package StreamDemo;import java.util.Arrays;import java.util.Comparator;import java.util.List;public class Main {  public static void main(String[] args) {    List<Program> listP =        Arrays.asList(new Program("Java", 5),                new Program("PHP", 2),                new Program("C#", 1));    Program maxExp = listP.stream()            .max(Comparator.comparing(Program::getExp))            .get();    System.out.println(maxExp);    long count = listP.stream().count();    System.out.println(count);   }}