package com.example.demo.Entity;import javax.persistence.*;import java.util.Date;@Entity@Table(name = "product")public class Product {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private int id;    @Column(name = "productname", nullable = false)    private String productname;    @Column(name = "expireddate", nullable = false)    private Date expireddate;    @Column(name = "producer", nullable = false)    private String producer;    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getProductname() {        return productname;    }    public void setProductname(String productname) {        this.productname = productname;    }    public Date getExpireddate() {        return expireddate;    }    public void setExpireddate(Date expireddate) {        this.expireddate = expireddate;    }    public String getProducer() {        return producer;    }    public void setProducer(String producer) {        this.producer = producer;    }}