package RKB2;import java.util.Scanner;public class Chuoi {  public static void main(String[] args) {    Scanner sc = new Scanner(System.in);    System.out.print("Nhap vao chuoi: ");    String chuoi = sc.nextLine();    do {      System.out.println("*********************MENU*******************");      System.out.println("1. Dao nguoc chuoi");      System.out.println("2. Tach chuoi ra sap xep theo alphabet");      System.out.println("3. In ra chu theo index nhap vao");      System.out.println("4. In ra chuoi co so ky tu bang so nhap vao");      System.out.println("5. Thoat");      System.out.print("Su lua chon cua ban: ");      int choice = Integer.parseInt(sc.nextLine());      switch (choice) {        case 1:          String[] words = chuoi.split(" ");          for (int i = words.length-1; i >= 0; i--) {            System.out.print(words[i] + " ");          }          System.out.println();          break;        case 2:          String[] words1 = chuoi.split(" ");          String temp;          for (int i = 0; i < words1.length; i++){            for (int j = i + 1; j < words1.length; j++){              if(words1[i].compareTo(words1[j]) > 0){                temp = words1[i];                words1[i] = words1[j];                words1[j] = temp;              }            }          }          System.out.println("Chuoi sau khi sap xep: ");          for (String w : words1) {            System.out.print(w + " ");          }          System.out.println();          break;        case 3:          System.out.print("Nhap vi tri chu cai muon xem: ");          int n = Integer.parseInt(sc.nextLine());          String[] words3 = chuoi.split(" ");          System.out.printf("Chu cai o vi tri thu %d la: ", n);          System.out.println(words3[n-1]);          break;        case 4:          System.out.print("Nhap so chu cai muon xem: ");          int m = Integer.parseInt(sc.nextLine());          String[] words4 = chuoi.split(" ");          System.out.printf("Chuoi gom co %d ky tu tach ra tu chuoi goc la: ", m);          for(int i = 0 ; i < m; i++){            System.out.print(words4[i] + " ");          }          System.out.println();          break;        case 5:          System.exit(0);          break;        default:          System.out.println("Error");      }    } while (true);  }}