package rikkei.restfulapidemo.Controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import rikkei.restfulapidemo.Entity.Product;import rikkei.restfulapidemo.Service.ProductService;import java.util.Optional;@RestControllerpublic class ProductController {    @Autowired    private ProductService productService;    @RequestMapping("/products")    public ResponseEntity<Object> getProduct(){    return new ResponseEntity<>(productService.getAllProduct(), HttpStatus.OK);    }    @PostMapping(value = "/products")    public ResponseEntity<Object> addProduct(@RequestBody Product product){        productService.save(product);        return new ResponseEntity<>("Add Success!", HttpStatus.CREATED);    }    @PutMapping(value = "/products/{id}")    public ResponseEntity<Object> updateProduct(@PathVariable("id") int id, @RequestBody Product product){        Optional<Product> productbyId = productService.findById(id);        if(productbyId.isPresent()){            Product p = productbyId.get();            p.setProductname(product.getProductname());            p.setQuantity(product.getQuantity());            p.setDes((product.getDes()));            productService.save(p);            return new ResponseEntity<>("Edited", HttpStatus.OK);        }        else{            return new ResponseEntity<>("Not found",HttpStatus.NOT_FOUND);        }    }    @DeleteMapping(value = "products/{id}")    public ResponseEntity<Object> deleteProduct(@PathVariable("id") int id){        productService.deleteProduct(id);        return new ResponseEntity<>("Deleted", HttpStatus.OK);    }}