package rikkei.restfulapidemo.Service;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import rikkei.restfulapidemo.Entity.Product;import rikkei.restfulapidemo.Repository.ProductRepository;import java.util.List;import java.util.Optional;@Servicepublic class ProductServiceImpl implements ProductService{    @Autowired    private ProductRepository productRepository;    @Override    public List<Product> getAllProduct() {        return (List<Product>) productRepository.findAll();    }    @Override    public void save(Product product) {        productRepository.save(product);    }    @Override    public void deleteProduct(int id) {        productRepository.deleteById(id);    }    @Override    public Optional<Product> findById(int id) {        return productRepository.findById(id);    }}